const Student = {
    fName:"",
    lName:"",
  };

function handleClick() {
    var items = this.state.items;
    const student = Object.create(Student);
    student.fName = this.state.fName;
    student.lName = this.state.lName;

    items.push(student);

    this.setState({
      items: items,
      fName: "",
      lName: "",
    });
  }

  export default handleClick;
