import React, { Component } from "react";
import "./App.css";

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      message: "",
      items: [],
    };
  }

  updateMessage(event) {
    this.setState({
      message: event.target.value,
    });
  }


  handleClick() {
    var items = this.state.items;

    items.push(this.state.message);

    this.setState({
      items: items,
      message: "",
    });
  }

  handleItemChanged(i, event) {
    var items = this.state.items;
    items[i] = event.target.value;


    this.setState({
      items: items,
    });
  }

  handleItemDeleted(i) {
    var items = this.state.items;

    items.splice(i, 1);

    this.setState({
      items: items,
    });
  }

  renderRows() {
    var context = this;

    return this.state.items.map(function (o, i) {
      return (
        <tr key={"item-" + i}>
          <td>
            <input
              type="text"
              value={o}
              onChange={context.handleItemChanged.bind(context, i)}
            />
          </td>

          <td>
            <button className="btn btn-danger" onClick={context.handleItemDeleted.bind(context, i)}>
              Delete
            </button>
          </td>
        </tr>
      );
    });
  }

  render() {
    return (
      <div className="container mt-5">
        <table className="table">
            <tr>
              <th>Name</th>
              <th>Action</th>
            </tr>
          <tbody>{this.renderRows()}</tbody>
        </table>


        <table className="table">
          <thead>
            <tr>
              <th>
              <input
          type="text"
          placeholder='Name' 
          value={this.state.message}
          onChange={this.updateMessage.bind(this)}
        />
              </th>

              <th>
              <button className="btn btn-success" onClick={this.handleClick.bind(this)}>Add Item</button>
              </th>
            </tr>
          </thead>
        </table>
      </div>
    );
  }
}
